import { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Form from 'react-bootstrap/Form';
import axios from 'axios';
import './Rant.css';

const People =() => {
  // setup state
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState("");
  const [problem, setProblem] = useState("");

  const fetchPosts = async() => {
    try {      
      const response = await axios.get("/api/posts");
      setPosts(response.data.posts);
    } catch(error) {
      setError("error retrieving posts: " + error);
    }
  }
  const createPost = async() => {
    try {
      await axios.post("/api/posts", {problem: problem});
    } catch(error) {
      setError("error adding a post: " + error);
    }
  }
  const deleteOnePost = async(post) => {
    try {
      await axios.delete("/api/posts/" + post.id);
    } catch(error) {
      setError("error deleting a post" + error);
    }
  }

  // fetch ticket data
  useEffect(() => {
    fetchPosts();
  },[]);

  const addPost = async(e) => {
    e.preventDefault();
    await createPost();
    fetchPosts();
    setProblem("");
  }

  const deletePost = async(post) => {
    await deleteOnePost(post);
    fetchPosts();
  }

  // render results
  return (
    <div className="container">
        <div className="App">
            {error}
            <h1>Create a Rant</h1>
            <form onSubmit={addPost}>
            <Card>
                <Card.Header>Rant</Card.Header>
                <Card.Body>
                        <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
                            <Form.Control as="textarea" rows={3}  value={problem} onChange={e=>setProblem(e.target.value)}/>
                        </Form.Group>
                    <Button className="float-end" type="submit" variant="primary">Submit</Button>
                </Card.Body>
            </Card>
            </form>
        </div>
    </div>
  );
}

export default People;
