import { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Form from 'react-bootstrap/Form';
import axios from 'axios';
import './Users.css';

const People =() => {
  // setup state
  const [users, setUsers] = useState([]);
  const [error, setError] = useState("");
  const [name, setName] = useState("");
  const [place, setPlace] = useState("");

  const fetchUsers = async() => {
    try {      
      const response = await axios.get("/api/users");
      setUsers(response.data.users);
    } catch(error) {
      setError("error retrieving users: " + error);
    }
  }
  const createUser = async() => {
    try {
      await axios.post("/api/users", {name: name, place: place});
    } catch(error) {
      setError("error adding a user: " + error);
    }
  }
  const deleteOneUser = async(user) => {
    try {
      await axios.delete("/api/users/" + user.id);
    } catch(error) {
      setError("error deleting a user" + error);
    }
  }

  // fetch ticket data
  useEffect(() => {
    fetchUsers();
  },[]);

  const addUser = async(e) => {
    e.preventDefault();
    await createUser();
    fetchUsers();
    setName("");
    setPlace("");
  }

  const deleteUser = async(user) => {
    await deleteOneUser(user);
    fetchUsers();
  }

  // render results
  return (
    <div className="container">
        <div className="App">
            {error}
            <h1>Create a User</h1>
            <form onSubmit={addUser}>
            <Card>
                <Card.Header>User</Card.Header>
                <Card.Body>
                    <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
                        <Form.Label>Username</Form.Label>
                        <Form.Control type="text" placeholder="Secret Identity" />
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="exampleForm.ControlInput2">
                        <Form.Label>Place</Form.Label>
                        <Form.Control type="text" placeholder="State" />
                    </Form.Group>
                    <Button className="float-end" type="submit" variant="primary">Submit</Button>
                </Card.Body>
            </Card>
            </form>
        </div>
    </div>
  );
}

export default People;
