import { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import axios from 'axios';
import './Home.css';

const Home =() => {
  // setup state
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState("");
  const [problem, setProblem] = useState("");

  const fetchPosts = async() => {
    try {      
      const response = await axios.get("/api/posts");
      setPosts(response.data.posts);
    } catch(error) {
      setError("error retrieving posts: " + error);
    }
  }
  const createPost = async() => {
    try {
      await axios.post("/api/posts", {problem: problem});
    } catch(error) {
      setError("error adding a post: " + error);
    }
  }
  const deleteOnePost = async(post) => {
    try {
      await axios.delete("/api/posts/" + post.id);
    } catch(error) {
      setError("error deleting a post" + error);
    }
  }

  // fetch ticket data
  useEffect(() => {
    fetchPosts();
  },[]);

  const addPost = async(e) => {
    e.preventDefault();
    await createPost();
    fetchPosts();
    setProblem("");
  }

  const deletePost = async(post) => {
    await deleteOnePost(post);
    fetchPosts();
  }

  // render results
  return (
    <div className="App">
      {error}
      <h1>Posts</h1>

        {posts.map( post => (
            <Card key={post.id} className="post">
              <Card.Header>Featured</Card.Header>
              <Card.Body>
                <Card.Title>Special title treatment</Card.Title>
                <Card.Text>
                  <p>{post.problem}</p>
                </Card.Text>
                <Button variant="primary" onClick={e => deletePost(post)}>Delete</Button>
              </Card.Body>
            </Card>
        ))}

    <h1>Create a Ticket</h1>
      <form onSubmit={addPost}>
        <div>
          <label>
            Problem:
            <textarea value={problem} onChange={e=>setProblem(e.target.value)}></textarea>
          </label>
        </div>
        <input type="submit" value="Submit" />
      </form>
    </div>
  );
}

export default Home;
